var model = {
    'dictionary': {
        'Persons' : {
            'pictures': {
                'community': ''
            },
            'items' : {
                'I' : {
                    'community': ''
                },
                'You': {
                    'community': ''
                },
                'We': {
                    'community': ''
                }
            }
        },
        'items': {
            'can' : {
                'community': ''
            },
            'need': {
                'community': ''
            },
            'please': {
                'community': ''
            },
            'thanks': {
                'community': ''
            }
        }
    },
    'contacts': {
    }
};

/*********
 * Phonegap-Bridge
 */
var pictureSource;   // picture source
var destinationType; // sets the format of returned value
document.addEventListener("deviceready",onDeviceReady,false);
function onDeviceReady() {
    pictureSource=navigator.camera.PictureSourceType;
    destinationType=navigator.camera.DestinationType;
}
/*********
 * Phonegap-Bridge-End
 */

var docg;
var selectedPath;
var sentence = [];
var username='community';
var receiver;
var usermodel;

function resetPool() {
    selectedPath = ['dictionary'];
    showPool();
}

function itemToHtml(cssClass, word, image) {
    return '<div title="' + word + '" class="' + cssClass + '"><img src="data:image/jpeg;base64,' + image + '" /><div>' + word + '</div></div>';
}

function showPool() {
    var subPool = docg.at(selectedPath).get();
    $('#pool').html('');

    $.each(subPool, function(i) {
        if(i !== 'items' && i !== 'pictures') {
            $(itemToHtml('category', i , this.pictures.community)).appendTo('#pool');
        }
    });

    if(subPool.items) {
        $.each(subPool.items, function(i) {
            $(itemToHtml('word', i, this.community)).appendTo('#pool');
        });
    }
    
    $('<a href="#chat_new_word" data-rel="dialog" class="add">+</div>').appendTo('#pool');

    $('#pool .word').hammer({prevent_default: false}).bind('hold', function(ev) {
        takePicture(function(imageData) {
            docg.at(selectedPath.concat(['items', $(ev.target).attr('title'), 'community'])).set(imageData);
        });
    });
}

function showSentence() {
    $('#sentence').html('');
    $.each(sentence, function(i) {
        var word = Object.keys(sentence[i])[0];
        $(itemToHtml('word', word, sentence[i][word].community)).appendTo('#sentence');
    });
    $('<div class="add">+</div>').appendTo('#sentence').on('click', resetPool);
}

function takePicture(callback) {
    // Take picture using device camera and retrieve image as base64-encoded string
    if(!navigator.camera) {
        alert('No Camera detected!');
        callback('');
    } else {
        navigator.camera.getPicture(function(imageData) {
                callback(imageData);
                showPool();
                showSentence();
            }, function(msg) {
                alert(msg);
            }, {
                quality: 50,
                targetWidth: 40,
                targetHeight: 40,
                destinationType: destinationType.DATA_URL
        });
    }
}


function showContacts() {
    $('#contact_list').html('');
    $.each(docg.at(['contacts']).get(), function(i) {
        $(itemToHtml('contact', i, docg.at(['contacts']).get()[i].picture)).appendTo('#contact_list').click(function() {
            receiver=i;
            $('#messages h1').text('Chat with ' + i);
            $('#chat h1').text('Write with ' + i);
            showMessages();
            $.mobile.changePage($('#messages'));
        });
    });
}

function showMessages() {
    $('#message_list').html('No Messages with ' + receiver);
    if(receiver) {
        var messages = usermodel.at(['messages', receiver]).get();
        if(messages && messages.length) {
            $('#message_list').html('');
            $.each(messages, function(j) {
                $('<span>' + $.timeago(new Date(messages[j].time)) + '</span>').appendTo('#message_list');
                $.each(messages[j].sentence, function(i) {
                    var word = Object.keys(messages[j].sentence[i])[0];
                    $(itemToHtml('word', word, messages[j].sentence[i][word].community)).appendTo('#message_list');
                });
                $('<hr style="clear:both" />').appendTo('#message_list');
            });
        }
    }
}


$(function() {

    showSentence();

    $('#pool .category').live('click', function() {
        selectedPath.push($(this).attr('title'));
        showPool();
    });

    $('#pool .word').live('click', function() {
        //console.log([selectedPath, $(this).attr('title')]);
        var wordindex = $(this).attr('title');
        var word = {};
        word[wordindex] = docg.at(selectedPath).get().items[wordindex];
        sentence.push(word);
        showSentence();
        resetPool();
    });
    
    $('#chat #back').click(function() {
        if(selectedPath.length>1) {
            selectedPath.pop();
            showPool();
        }
    });

    $('#sentence .word').live('click', function() {
        sentence.splice($(this).index(), 1);
        showSentence();
    });
    
    
    $('#chat_new_word form').submit(function() {
        var new_word=$('#chat_new_word #word').val();
        var new_word_is_category=$('#chat_new_word #type option:selected').val() === 'yes';
        takePicture(function(imageData) {
            if(new_word_is_category) {
                docg.at(selectedPath.concat([new_word])).set({
                    'pictures' : {
                        'community': imageData
                    },
                    'items': {
                    }
                });
            } else {
                docg.at(selectedPath.concat(['items', new_word])).set({ 'community': imageData });
            }
        });
        $.mobile.changePage($('#chat'));
        return false;
    });
    
    $('#send_sentence').click(function() {
        sharejs.open('user_' + receiver, 'json', function(error, doc) {
            if(error) {
                alert(error);
            }

            if(!doc.at(['messages', username]).get()) {
                doc.at(['messages', username]).set([]);
            }
            
            doc.at(['messages', username]).push({
                'time': (new Date()).getTime(),
                'sentence': sentence
            }, function() {
                doc.close();
            });
        });
    });
    
    $('#login form').submit(function() {
        username=$('#login #username').val();
        $.mobile.changePage($('#home'));
        if(!docg.at(['contacts']).get()[username]) {
            alert("Welcome " + username + "! You are new in the system. Please make a picture of you.");
            takePicture(function(imageData) {
                docg.at(['contacts', username]).set({ 'picture' : imageData });
            });
        };
        sharejs.open('user_' + username, 'json', function(error, doc) {
            if(error) {
                alert(error);
                return;
            }
            
            if (doc.created) {
                doc.set({ messages : {} });
            }
            
            usermodel = doc;
            
            doc.on('change', function() {
                showMessages();
            });
        });
        return false;
    });

    sharejs.open('model', 'json', function(error, doc) {
        docg = doc;
        if (error) {
            alert(error);
            return;
        }
    
        if (doc.created) {
            doc.set(model);
        }
        
        model = doc.get();
        resetPool();
        showContacts();
        
        doc.on('change', function() {
            showPool();
            showSentence();
            showContacts();
        });
    });
    
});

